import './App.css'
import Search from './components/Search'
import Weather from './components/Weather'
import { useState, useEffect } from 'react'


const api = {
  key: '1ea4a48ba1c9e89c6d1a5e9077ad6693',
  base: 'https://api.openweathermap.org/data/2.5/onecall?',
}

function App() {
  const [c, setC] = useState('larisa')
  const [loading, setLoading] = useState(true)
  const [data, setData] = useState({})


  const fetchData = (geo) =>{
    fetch(`${api.base}lat=${geo[0].lat}&lon=${geo[0].lon}&exclude=hourly&appid=e3538febf824d275717c9ffc20afd2a3`)
     .then((res) => res.json())
     .then((result) => {
       setData(result)
       setLoading(false)
     })
   }

  const search = async () => {
    setLoading(true)

    fetch(`http://api.openweathermap.org/geo/1.0/direct?q=${c}&limit=5&appid=${api.key}`)
      .then((res) => res.json())
      .then((result) => {
        fetchData(result);
      })

  }

  useEffect(() => {
    search();
  }, [c]);




  return (
    <div>
      <div className="container">
        <div className="card">
          <Search city={setC}></Search>
          <h2 className="city">{`Weather in ${c}`}</h2>
          <Weather data={data.current} loading={loading}></Weather>
        </div>
      </div>
    </div>
  )
}

export default App
